#!/bin/bash

sudo dnf update --refresh -y && sudo dnf install https://download1.rpmfusion.org/free/fedora/rpmfusion-free-release-$(rpm -E %fedora).noarch.rpm https://download1.rpmfusion.org/nonfree/fedora/rpmfusion-nonfree-release-$(rpm -E %fedora).noarch.rpm -y && sudo dnf install xorg-x11-drivers xorg-x11-xinit xorg-x11-drv-nvidia-340xx akmod-nvidia-340xx -y && sudo dnf install xorg-x11-drv-nvidia-340xx-cuda -y && sudo dnf update -y && sudo dnf install xorg-x11-drv-fbdev xorg-x11-drv-synaptics pulseaudio alsa-plugins-pulseaudio pulseaudio-module-x11 pulseaudio-utils pavucontrol xbacklight gvfs ffmpeg nano rxvt ncmpcpp libmpd dunst i3 i3status i3lock dmenu feh lxappearance mpv deja-dup gimp pidgin leafpad steam VirtualBox virtualbox-guest-additions ubuntu-title-fonts adobe-source-code-pro-fonts terminus-fonts terminus-fonts-console -y && sudo dnf install lpf-spotify-client && lpf approve spotify-client && sudo -u pkg-build lpf build spotify-client && sudo dnf install /var/lib/lpf/rpms/spotify-client/spotify-client-*.rpm && echo 'exec i3' >> ~/.xinitrc && cp i3lock.service /etc/systemd/system/ && systemctl enable i3lock.service && reboot
